local function lerp(v1, v2, percentage)
	return (1-percentage)*v1 + percentage*v2 -- Linear Interpolation
end

local function distance(x1, y1, x2, y2)
	return math_sqrt((x2-x1)*(x2-x1) + (y2-y1)*(y2-y1))
end



